//If no tokens are selected this Macro wont work
if (canvas.tokens.controlled == 0) { ui.notifications.error("Please Select one or more Actors") } else {
    //Declare Variables
    var attackArrayCombined = new Array
    let actors = canvas.tokens.controlled.map(token => {
        return token.actor
    });
    //Do this for each actor selected
    for (let actor of actors) {
        //more Variables to be declared (But inside this loop)
        let myArray = actor.data.items
        var attackSet = new Set
        const attackArray = myArray
            .filter((item) => item.type === 'attack') // filter to only attacks
            .map((item) => { //map over each item and if it's a duplicate add a blank else? add it to the Array and set 
                const name = `${item.name}`;
                if (attackSet.has(name)) {
                    return { name: '' }
                } else {
                    attackSet.add(name)
                    return { name }
                }
            }
            );

        const attackArrayNew = attackArray.filter((item) => item.name !== '') //filter out blanks

        attackArrayCombined.push(attackArrayNew)//Combine the Arrays that were generated for each actor into one place
    }
    let flattened = attackArrayCombined.reduce((a, b) => a.concat(b), []); //flatten it out so next command can count

    let counts = flattened.reduce(                                       //count each attack in arrays to compare to the number of actors selected later
        (map, { name }) => map.set(name, (map.get(name) || 0) + 1), new Map()
    );
    let found = attackArrayCombined[0].filter(({ name }) => counts.get(name) === attackArrayCombined.length); //Compare the number of Duplicates of each attack to the Number of Actors selected (to make sure they all have the attack)
    if (found == 0) {
        ui.notifications.error("Either Selected Actor has no Attacks or Selected Actors do not have common attacks, Cannot Batch Roll")//if actors have no attacks in common then give an error and stop
    } else { //if they do...
        var data = { attacks: found } //so it makes sense to handlebars
        const contentRaw = `
      <form>
    <div class="form-group" >
    <label id='attackDataHere'>Available Attacks:</label>   
    <select id="choices" name="choices">
           {{#each attacks}}<option value="{{name}}">{{name}}</option>{{/each}}
          </select>
        </div>
  <em>Select attack that you want to roll for selected tokens. by default will roll first valid attack on respective character sheet.</em>
      </form>
    `//HTML template so that we can have a drop down
        const template = Handlebars.compile(contentRaw);//give the html template to handlebars
        const content = template(data); //use our attack data from before to insert into template
        let selectedOption; //declaring another variable :D
        //Make a dialog and make it do things and stuff
        let dialog = new Dialog({
            title: "Attack Selection",
            content: content, //this is where that HTML ends up
            //what do you want the buttons to do?
            buttons: {
                yes: {
                    icon: '<i class="fas fa-dice"></i>',
                    label: "Attack",
                    callback: () => {
                        selectedOption = document.getElementById('choices').value;
                    },
                },
                no: {
                    icon: '<i class="fas fa-times"></i>',
                    label: "Cancel",
                }
            },
            default: "yes", //Default point to yes
            close: () => {
                if (selectedOption) {
                    switch (selectedOption) {
                        default: //the default option for the Dialog, it was easy to put the code here so i did
                            let actors = canvas.tokens.controlled.map(token => { //gotta declare the actors again
                                return token.actor
                            })
                            for (let actor of actors) { //another for loop for each of the actors so they each do their attack
                                var itemFound = actor.items.find(item => item.name === `${selectedOption}` && item.type === "attack") // ${selectedOption} is the name of the attack(sometimes shared with weapons) 
                                //so we don't want any weapons from your character sheet only the attacks they produce
                                let item = itemFound.useAttack({ skipDialog: true }) // Skip dialog will use the attack without making a bunch of other pop ups 
                                item
                            }
                    }
                }
            }
        }
        );
        dialog.render(true); //RENDER!!!!
    }
}
